import React, { Fragment, useContext, useEffect, useState } from 'react'
import CartIcon from '../../Cart/CartIcon'
import classes from './HeaderCartButton.module.css'
import CartContext from '../../../store/cart-context'

const HeaderCartButton = (props) => {
    const [btnIsHighLighetd, setBtnIsHighLighted] = useState(false);
    const cartCtx = useContext(CartContext);
    const numberOfCartItems = cartCtx.items.reduce((curNumber, item) => { return (curNumber + item.amount) }, 0);

    const { items } = cartCtx;

    const btnClassses = `${classes.button} ${btnIsHighLighetd ? classes.bump : ''}`


    useEffect(() => {
        if (cartCtx.items.lengt === 0) {
            return;
        }
        setBtnIsHighLighted(true);
    }, [cartCtitemsx])

    return (
        <Fragment>
            <button className={classes.button} onClick={props.onShowCart}>
                <span className={classes.icon}>
                    <CartIcon />
                </span>
                <span >
                    Your Cart
                </span>
                <span className={classes.badge}>
                    {numberOfCartItems}
                </span>
            </button>
        </Fragment>
    )
}

export default HeaderCartButton
